// Setup namespaces for outbound CDA
var xsd = new Namespace("xsd", "http://www.w3.org/2001/XMLSchema");
var xsi = new Namespace("xsi", "http://www.w3.org/2001/XMLSchema-instance");
var v3 = new Namespace("v3", "urn:hl7-org:v3");
default xml namespace = v3;

// This function is useful to help switch namespaces whenever reading a data element from
// the CSV file since the namespace on that file differs from that of the outbound CDA.
function msgRead(col) {
  default xml namespace = "";
  var data = msg['row']['column' + col].toString();
  default xml namespace = v3;
  return (data);
}

// Trim leading and trailing whitespace from a text string
function trim(str) {
  return str.replace(/^\s\s*/, '').replace(/\s\s*$/, '');
}

step.script:
var idxHeaderBase = 1;
var idxHeaderOffsets = {
  'facName':0, 'facCode':1, 'sourceSystem':2, 'pid':3, 'fname':4, 'lname':5, 'sex':6
};

// Set some channel variables for use in later mappings
channelMap.put('facName', trim(msgRead(idxHeaderBase + idxHeaderOffsets['facName'])));
channelMap.put('pid', trim(msgRead(idxHeaderBase + idxHeaderOffsets['pid'])));
channelMap.put('softwareName', channelMap.get('facName') + " ADT-Companion v.1.0");
channelMap.put('currDTZ', DateUtil.getCurrentDate("yyyyMMddHHmmssZ"));
channelMap.put('currDT', DateUtil.getCurrentDate("yyyy-MM-dd HH:mm:ss"));
var facCode = msgRead(idxHeaderBase + idxHeaderOffsets['facCode']);
if (trim(facCode) != "" && facCode != null) {
  channelMap.put('facCode', trim(facCode));
} else {
  channelMap.put('facCode', '1');
}

// Pull out some other values to be used as index fields in the CDS
channelMap.put('firstName', trim(msgRead(idxHeaderBase + idxHeaderOffsets['fname'])));
channelMap.put('lastName', trim(msgRead(idxHeaderBase + idxHeaderOffsets['lname'])));
channelMap.put('birthDate', '');
channelMap.put('gender', "M".equalsIgnoreCase(msgRead(idxHeaderBase + idxHeaderOffsets['sex'])) ||
                         "Male".equalsIgnoreCase(msgRead(idxHeaderBase + idxHeaderOffsets['sex'])) ?
                         "M" : "F".equalsIgnoreCase(msgRead(idxHeaderBase + idxHeaderOffsets['sex'])) ||
                         "Female".equalsIgnoreCase(msgRead(idxHeaderBase + idxHeaderOffsets['sex'])) ? "F" : "N");
channelMap.put('sourceSystem', trim(msgRead(idxHeaderBase + idxHeaderOffsets['sourceSystem'])));

// Start populating header elements
tmp['id']['@extension'] += "_" + channelMap.get('facName') + "_" + facCode + "_" + channelMap.get('pid') +
  "_" + channelMap.get('currDTZ');
tmp['title'] += " - " + msgRead(idxHeaderBase + idxHeaderOffsets['fname']) +
  " " + msgRead(idxHeaderBase + idxHeaderOffsets['lname']) + " - " +
  channelMap.get('currDTZ');
tmp['effectiveTime']['@value'] = channelMap.get('currDTZ');
tmp['recordTarget']['patientRole']['id']['@extension'] = channelMap.get('pid');
tmp['recordTarget']['patientRole']['patient']['name']['family'] =
  msgRead(idxHeaderBase + idxHeaderOffsets['lname']);
tmp['recordTarget']['patientRole']['patient']['name']['given'] =
  msgRead(idxHeaderBase + idxHeaderOffsets['fname']);
tmp['recordTarget']['patientRole']['patient']['administrativeGenderCode']['@code'] =
  "M".equalsIgnoreCase(msgRead(idxHeaderBase + idxHeaderOffsets['sex'])) ||
  "Male".equalsIgnoreCase(msgRead(idxHeaderBase + idxHeaderOffsets['sex'])) ?
  "M" : "F".equalsIgnoreCase(msgRead(idxHeaderBase + idxHeaderOffsets['sex'])) ||
  "Female".equalsIgnoreCase(msgRead(idxHeaderBase + idxHeaderOffsets['sex'])) ? "F" : "";
tmp['recordTarget']['patientRole']['providerOrganization']['name'] = channelMap.get('facName');
tmp['author']['time']['@value'] = channelMap.get('currDTZ');
tmp['author']['assignedAuthor']['id']['@extension'] = channelMap.get('softwareName');
tmp['author']['assignedAuthor']['assignedAuthoringDevice']['softwareName'] = channelMap.get('softwareName');
tmp['author']['assignedAuthor']['representedOrganization']['id']['@extension'] = facCode;
tmp['author']['assignedAuthor']['representedOrganization']['name'] = channelMap.get('facName');

// If there are any remaining required values that are empty, replace with nullFlavor="UNK":

// <administrativeGenderCode code="" .../> => <administrativeGenderCode nullFlavor="UNK"/>
if (tmp['recordTarget']['patientRole']['patient']['administrativeGenderCode']['@code'] == "") {
  delete tmp['recordTarget']['patientRole']['patient']['administrativeGenderCode']['@code'];
  delete tmp['recordTarget']['patientRole']['patient']['administrativeGenderCode']['@codeSystem'];
  tmp['recordTarget']['patientRole']['patient']['administrativeGenderCode']['@nullFlavor'] = "UNK";
}

