  tmpText['td'][0] = medName;
  tmpText['td'][1] = visitDate.substring(5, 7) + "/" + visitDate.substring(8, 10) + "/" + visitDate.substring(0, 4);
  tmpText['td'][2] = msgRead(idxMedBases['medDose'] + (i * nextMedOffset));
  tmpText['td'][3] = msgRead(idxMedBases['medDays'] + (i * nextMedOffset));
  // Done populating text elements

  // Append populated text node
  tmp['component']['structuredBody']['component']['section']['text']['table']['tbody'].appendChild(tmpText);
}

entry.string:
var maxMeds = 100;
var idxMedBases = {
  'medName':12, 'medDate':11, 'medDose':13, 'medDays':14
};
var nextMedOffset = 6;

tmp['component']['structuredBody']['component']['section']['id']['@extension'] += "_" +
  channelMap.get('facName') + "_" + facCode + "_" + channelMap.get('pid') + "_" + channelMap.get('currDTZ');

var textTmpl = tmp['component']['structuredBody']['component']['section']['text']['table']['tbody']['tr'].copy();
delete tmp['component']['structuredBody']['component']['section']['text']['table']['tbody']['tr'];
for (var i = 0; i < maxMeds; i++) {
  var medName = msgRead(idxMedBases['medName'] + (i * nextMedOffset));
  if (medName == '' || medName == null) continue;

  var tmpText = textTmpl.copy();
  var visitDate = msgRead(idxMedBases['medDate'] + (i * nextMedOffset));

  // Populate text elements
  tmpText['td'][0] = medName;
  tmpText['td'][1] = visitDate.substring(5, 7) + "/" + visitDate.substring(8, 10) + "/" + visitDate.substring(0, 4);
  tmpText['td'][2] = msgRead(idxMedBases['medDose'] + (i * nextMedOffset));
  tmpText['td'][3] = msgRead(idxMedBases['medDays'] + (i * nextMedOffset));
  // Done populating text elements

  // Append populated text node
  tmp['component']['structuredBody']['component']['section']['text']['table']['tbody'].appendChild(tmpText);
}

