entry.string:
var query = new XML(Packages.java.net.URLDecoder.decode(msg['Parameters']['query'].toString()));

channelMap.put('cda_id', query['cdaID']);
channelMap.put('id', query['identification']);

properties.property:
// The cds database name, host, port, user & password must be correctly configured in the following connection line:
var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver',
                                                                'jdbc:mysql://localhost/cds',
                                                                'oecuser',
                                                                'yourPassword');
var cda_id = channelMap.get('cda_id');
var id = channelMap.get('id');

var column = null;
var value = null;

// We query by one column, preferring the unique cda_id (ignoring the other if set)
if (typeof cda_id != undefined && cda_id.toString().length > 0) {
	column = 'cda_id';
    value = cda_id.toString();
} else if (typeof id != undefined && id.toString().length > 0) {
	column = 'patient_clinical_id';
    value = id.toString();
} else {
    var response = ResponseFactory.getFailureResponse("Either <cda_id/> or <identification/> must be defined");
    responseMap.put("httpResponse", response);
    return;
}

var qs = "SELECT cda_id, cda FROM CDA WHERE " + column + " = ?";
var params = java.util.ArrayList()
params.add(value);
result = dbConn.executeCachedQuery(qs, params);

// Return zero or more matching CDAs in an xml doc.
var doc = <query_response/>;
while(result.next()) {
    var record = <record/>;
	record.cda_id = result.getInt(1);
    record.cda = FileUtil.encode(result.getString(2).getBytes());
    doc.appendChild(record);
}
// This *should* provide the XML declaration, but it doesn't: doc = new XML(doc);
// Add it by hand:
doc = '<?xml version="1.0" encoding="UTF-8" standalone="yes" ?>' + doc.toString()
dbConn.close();

// This stores an HTTP 200 (success) with the body 'cda' as 
// set above in the response variable "httpResponse", which
// should be selected as the "Respond From" variable on the source
// tab.
var response = ResponseFactory.getSuccessResponse(doc);
responseMap.put("httpResponse", response);

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;

channel.shutdownScript:
// This script executes once when the channel is undeployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;
